    userDomain: "acme.com",
    password: "",
});
// Creates Credential Pool of Type "USERNAME_PASSWORD"
const _this = new zpa.PraCredentialPool("this", {
    name: "PRACredentialPool01",
    credentialType: "USERNAME_PASSWORD",
