    description: "PRA_Example",
    enabled: true,
    healthReporting: "ON_ACCESS",
    bypassType: "NEVER",
    isCnameEnabled: true,
    tcpPortRanges: [
        "22",
        "22",
        "3389",
        "3389",
    ],
    domainNames: [
        "ssh_pra.example.com",
        "rdp_pra.example.com",
    ],
    segmentGroupId: zpa_segment_group["this"].id,
    serverGroups: [{
        ids: [zpa_server_group["this"].id],
